import { Component, Input, Output, EventEmitter } from '@angular/core';

@Component({
  selector: 'media-item',
  templateUrl: './media-item.component.html',
  styleUrls: ['./media-item.component.css']
})
export class MediaItemComponent {
  @Input() mediaItem: any;
  @Output() delete = new EventEmitter();

  onDelete() {
    console.log("Deleted");
    this.delete.emit(this.mediaItem);
  }
}
<h2>{{ mediaItem.name | uppercase | slice: 0: 10 }}</h2>
<div *ngIf="mediaItem.watchedOn">{{ mediaItem.watchedOn | date }}</div>
<ng-template [ngIf]="mediaItem.category">
    <div>{{ mediaItem.category }}</div>
</ng-template>
<div>{{ mediaItem.year }}</div>
<div [mwFavourite]="mediaItem.isFavourite" class="tools">
    <a (click)="onDelete()" class="delete">
        remove
    </a>
    <a class="details">
        watch
    </a>
</div>
