import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-square',
  templateUrl: './square.component.html',
  styleUrls: ['./square.component.css'],
})
export class SquareComponent {
  @Input() value: 'X' | 'O' | undefined;
}
<button *ngIf="!value" class="button">{{ value }}</button>
<button *ngIf="value == 'X'" class="button X">{{ value }}</button>
<button *ngIf="value == 'O'" class="button O">{{ value }}</button>
