import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-board',
  templateUrl: './board.component.html',
  styleUrls: ['./board.component.css'],
})
export class BoardComponent {
  squares: any[];
  xIsNext: boolean | undefined;
  winner: string | undefined | null;
  fullGrid: boolean | undefined;

  constructor() {
    this.squares = Array(9).fill(null);
  }

  ngOnInit() {
    this.newGame();
  }

  newGame() {
    this.squares = Array(9).fill(null);
    this.winner = null;
    this.xIsNext = true;
    this.fullGrid = false;
  }

  get player() {
    return this.xIsNext ? 'X' : 'O';
  }

  makeMove(idx: number) {
    if (this.squares[idx] === null && this.winner === null && !this.fullGrid) {
      this.squares.splice(idx, 1, this.player);
      this.xIsNext = !this.xIsNext;
    }

    this.winner = this.calculateWinner();

    this.fullGrid = true;
    for (let i = 0; i < this.squares.length; i++) {
      if (!this.squares[i]) {
        this.fullGrid = false;
        continue;
      }
    }
  }

  calculateWinner() {
    const lines = [
      [0, 1, 2],
      [3, 4, 5],
      [6, 7, 8],
      [0, 3, 6],
      [1, 4, 7],
      [2, 5, 8],
      [0, 4, 8],
      [2, 4, 6],
    ];

    for (let i = 0; i < lines.length; i++) {
      const [a, b, c] = lines[i];
      if (
        this.squares[a] &&
        this.squares[a] === this.squares[b] &&
        this.squares[a] === this.squares[c]
      ) {
        return this.squares[a];
      }
    }
    return null;
  }
}
<div class="header-bar">
  <h1>Tic Tac Toe</h1>
  <a class="label" href="newGame()">New Game</a>
</div>

<ul class="message">
  <li>Current Player: {{ player }}</li>
</ul>

<main>
  <app-square
    *ngFor="let val of squares; let i = index"
    [value]="val"
    (click)="makeMove(i)"
  >
  </app-square>
</main>

<ul class="message">
  <li *ngIf="winner">Player {{ winner }} won the game.</li>
  <li *ngIf="!winner && fullGrid">The game was a draw.</li>
</ul>
